import React from 'react';
import { GoogleApiWrapper, InfoWindow, Map, Marker } from 'google-maps-react';

// import Paper from 'material-ui/Paper';
// import Typography from 'material-ui/Typography';
// import { typography } from 'material-ui/styles';
const libraryMarkers = [];

class GoogleMapsContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showingInfoWindow: false,
            activeMarker: {},
            selectedPlace: {},
        }
        // binding this to event-handler functions
        this.onMarkerClick = this.onMarkerClick.bind(this);
        this.onMapClick = this.onMapClick.bind(this);
    }
    componentDidMount() {
      this.getLibraryMarkerLocations();
    }

    onMarkerClick = (props, marker, e) => {
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });
    }
    onMapClick = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            });
        }
        console.log(this.props.location);
    }
    getLibraryMarkerLocations = () => {
      this.props.libraries.map((place) => {
        libraryMarkers.push(place.geometry.location);
      })
      console.log(libraryMarkers);
    }

    render() {
        const style = {
            width: '50vw',
            height: '75vh',
            'marginLeft': 'auto',
            'marginRight': 'auto'
        }

//         const coolIcon = { 
//             url: 'data:image/svg+xml;utf8;base64,',
//         };
//         const coolIcon = { url: './styles/assets/cool.png', scaledSize: { width: 25, height: 25} };

        return (
            <Map
              item
              xs={12}
              style={style}
              google={this.props.google}
              onClick={this.onMapClick}
              zoom={14}
              initialCenter={this.props.location}       
            >
                {/* <Marker position={{lat:43.6532, lng:-79.3832}}/> */}

              {libraryMarkers.map((place) => {
                return (
                  <Marker position={place}/>
                )
              })}

              {/* // {lat:43.6532, lng:-79.3832} */}
                  {/* // onClick={this.onMarkerClick}
                    // icon={coolIcon} */}
                <InfoWindow
                    marker={this.state.activeMarker}
                    visible={this.state.showingInfoWindow}
                >
                    {/* <Paper>
                        <Typography
                            variant='headline'
                            component='h4'
                        >
                            Changing Colors Garage
                        </Typography>
                        <Typography
                            component='p'
                        >
                            98G Albe Dr Newark, DE 19702 <br />
                            302-293-8627
                        </Typography>
                    </Paper> */}
                </InfoWindow>
            </Map>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: ('AIzaSyAfczrbXkzBLOm5op0o4R96gdqjJaKHrm8')
})(GoogleMapsContainer)